mapper.insert:
	insert
	  into	tb_booking
	  	 (
	  	 	book_no
	  	 ,	cl_no
	  	 ,	user_no
	  	 ,	emp_no
	  	 ,	book_date
	  	 ,	book_stime
	  	 ,	book_etime
	     )
    values
    	 (
    	 	seq_book_no.nextval
    	 ,	#{clNo}
    	 ,	#{userNo}
    	 ,	#{empNo}
    	 ,	#{bookDate}
    	 ,	#{bookStime}
    	 ,	#{bookEtime}
    	 )

mapper.update:
update
		tb_booking
   set	book_date = #{bookDate}
     ,	book_stime = #{bookStime}
     ,	book_etime = #{bookEtime}
 where	book_no = #{bookNo}
