mapper.insert:
	insert 
	  into tb_user
  	  (
	     user_no
	   , user_name
	   , user_phone
	   , user_birth
	   , user_gender
	   , user_type
	   , user_sdate
	   , user_month
	   , user_profile_url
	   )
	   values
	   (
	     seq_user_no.nextval
	   , #{userName}
	   , #{userPhone}
	   , #{userBirth}
	   , #{userGender}
	   , #{userType}
	   , #{userSdate}
	   , #{userMonth}
	   , #{userProfileUrl}
	   )

mapper.update:
update
		tb_user
   set	user_name = #{userName}
     ,	user_phone = #{userPhone}
     ,	user_birth = #{userBirth}
     ,	user_type = #{userType}
     ,	user_month = #{userMonth}
 where	user_no = #{userNo}

update
		tb_user
   set	user_profile_url = #{userProfileUrl}
 where	user_no = #{userNo}
